(28, 'Gel tẩy tế bào chết Rosette Nhật Bản', 135000, 'tayTBC.jpg', 'Gel tẩy tế bào chết Rosette Nhật Bản là sản phẩm nổi tiếng giàu các loại axit trái cây vừa tẩy tế bào chết an toàn vừa tăng cường độ ẩm cần thiết cho da', 6, 8, 45, 5, '2023-11-13 02:31:10', '2023-11-13 02:31:10', 0, ''),
(29, 'Kem Chống Nắng La Prairie Cellular', 2000000, 'kemchongNang.jpg', 'Kem chống nắng La Prairie Cellular Swiss được thiết kế để sử dụng thay cho kem dưỡng ẩm. Sản phẩm đóng vai trò như tấm màn bảo vệ vũng chắc khỏi tia UVA, UVB và các tác nhân gây hại từ môi tr', 2, 8, 47, 3, '2021-05-13 02:31:10', '2021-05-13 02:31:10', 0, ''),
(31, 'Kem Chống Nắng Hatomugi', 93000, '1enter.jpg', 'Reihaku Hatomugi UV Milky Gel là dòng sản phẩm kem chống nắng dành cho da mặt và toàn thân đến từ thương hiệu mỹ phẩm Hatomugi của Nhật Bản', 2, 10, 48, 2, '2023-11-13 02:44:00', '2023-11-13 02:44:00', 0, ''),
(33, 'Kem Nền MAC Studio', 187000, 'knn.jpg', 'Kem Nền MAC Studio Fix Fluid SPF 15 Fond De Teint SPF 15 là kem nền dạng lỏng với công thức đặc biệt có khả năng kiềm dầu cực kì cao', 6, 11, 46, 4, '2023-11-13 02:46:26', '2023-11-13 02:46:26', 0, ''),
(34, 'Nước Hoa Nữ Versace Bright Crystal', 1500000, 'nnnn.jpg', 'Nước Hoa Nữ Versace Bright Crystal EDT Bright Crystal Eau de Toilette Spray là sản phẩm đến từ thương hiệu Versace nổi tiếng toàn thế giới.', 4, 13, 49, 1, '2023-11-14 02:48:48', '2023-11-15 10:44:01', 0, ''),
(35, 'Nước hoa Victoria’s Secret', 950000, 'NuocHoa.jpg', 'Victoria’s Secret Bombshell ra đời với thông điệp mang đến sự trẻ trung, tôn lên nét xinh đẹp của mọi cô gái. ', 4, 13, 46, 4, '2023-10-10 02:48:48', '2023-10-13 02:48:48', 0, ''),
(36, 'Nước Hoa Nam Charme', 965000, 'NHT5.jpg', 'Nước hoa Charme Boss mang hương thơm độc quyền, không giống bất cứ tone mùi nào. Chính vì vậy, nếu bạn có đam mê sưu tầm nước hoa thì hãy sở hữu cho mình chai nước hoa này nhé', 4, 13, 42, 8, '2021-05-13 02:52:03', '2021-05-13 02:52:03', 0, ''),
(37, 'Nước Hoa Chanel jean', 1051000, 'nhh.jpg', 'Nước Hoa Nữ Versace Bright Crystal EDT Bright Crystal Eau de Toilette Spray là sản phẩm đến từ thương hiệu Versace nổi tiếng toàn thế giới.', 4, 12, 48, 2, '2023-11-13 02:52:54', '2023-11-13 02:52:54', 0, ''),
