(39, 'Nước hoa Celine Rimbaud 100ml', 333555, 'clnn.jpg', ' Rimbaud của Celine là một loại nước hoa dành cho phụ nữ và nam giới. Rimbaud được ra mắt vào năm 2022.', 4, 16, 47, 3, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 0, ''),
(40, 'Nước hoa Celline Black', 900000, 'NH5.jpg', ' Black Tie EDP được tạo kiểu như một chiếc áo khoác dạ tiệc, gỗ tuyết tùng và rêu cây tạo nên vẻ quyến rũ của nó, trong khi lớp phấn phủ của hoa diên vĩ gây rắc rối cho cơ thể và tâm trí.', 4, 17, 49, 1, '2023-11-13 02:55:21', '0000-00-00 00:00:00', 0, ''),
(41, 'Kem Dưỡng Ẩm Neutrogena', 269000, 'kemduong.jpg', 'Kem Dưỡng Ẩm Neutrogena Hydro Boost Water Gel 50g là kem dưỡng ẩm đến từ thương hiệu mỹ phẩm Neutrogena của Mỹ', 6, 17, 49, 1, '2023-11-13 02:55:21', '2023-11-13 02:55:21', 0, ''),
(42, 'Son dưỡng môi Laneige Lip ', 75000, 'son.jpg', 'Kem dưỡng môi Laneige Lip Sleeping Mask được đánh giá cao về khả năng dưỡng ẩm và làm mềm môi ngoài ra còn giúp cải thiện tình trạng khô, nứt nẻ mang đến một đôi môi căng bóng và hồng hào.', 6, 18, 50, 0, '2023-05-13 02:58:47', '2023-11-15 03:30:00', 0, ''),
(43, 'Mặt Nạ Giấy INNISFREE', 19000, 'mng.jpg', 'Mặt Nạ Giấy Innisfree bao gồm 18 loại chiết xuất từ những nguyên liệu thiên nhiên khác nhau, cung cấp độ ẩm và dưỡng chất cần thiết đối với từng loại da và các vấn đề khác nhau của da.', 6, 18, 56, 0, '2023-11-13 02:59:52', '2023-11-13 03:59:52', 0, ''),
(44, 'Bông tẩy trang Ipek siêu mềm mịn ', 43000, 'btt.jpg', 'Bông tẩy trang Ipek với công nghệ sản xuất tiên tiến giúp tẩy trang và làm sạch mọi bụi bẩn bám trên da bằng nước sạch, nước tẩy trang hay nước hoa hồng chăm sóc da.', 6, 18, 50, 0, '2023-11-15 02:59:52', '2023-11-15 03:59:52', 0, ''),
(46, 'Nước tẩy trang Nivea Pearl White', 180000, 'ntttt.jpg', 'Nước Tẩy Trang Nivea Làm Sáng Da Pearl White Micellar Water 400Ml \r\nNước Tẩy Trang Nivea Sáng Da \r\nCho Da Thường Và Khô\r\n', 5, 15, 44, 6, '2021-05-13 03:14:08', '2021-05-13 03:14:08', 0, ''),
(47, 'Sữa Rửa Mặt Some By Mi Acne ', 235000, 'thbh.jpg', 'Sữa Rửa Mặt Some By Mi Acne Cho Da Mụn 100ml, các hoạt chất trên đem lại khả năng làm sạch và ngừa mụn vô cùng đáng nể.\r\n\r\n', 1, 1, 51, 0, '2023-11-13 06:54:46', '2023-11-13 06:54:46', 0, ''),
(48, 'Sữa rửa mặt Acne-Aid Liquid Cleanser', 175000, 'Vietttjpg', 'Sữa rửa mặt Acne-Aid Liquid Cleanser làm sạch da dịu nhẹ, giúp loại bỏ dầu cho da. Thích hợp sử dụng hàng ngày, đặc biệt cho da dầu và mụn.', 1, 1, 50, 0, '2023-11-13 06:57:07', '2023-11-13 06:57:07', 0, ''),
(49, 'Sữa rửa mặt bọt Hada Labo Gokujyun', 160000, 'grande.jpg', 'Sữa rửa mặt tạo bọt Hada Labo của Rohto Nhật Bản được thiết kế dạng tạo bọt sẵn tiện lợi giúp cho làn da sạch sâu tận lỗ sâu chân lông ', 1, 1, 127, 2, '2023-11-13 07:04:34', '2023-11-13 07:04:34', 0, ''),
(50, 'Sữa rửa mặt Reihaku Hatomugi', 190000, 'srm7.jpg', 'Sữa rửa mặt Hatomugi ngừa mụn, dưỡng ẩm và làm sáng da Reihaku Hatomugi Acne Care & Facial Washing 130g chứa các thành phần dưỡng ẩm tự nhiên chiết xuất từ hạt Ý dĩ', 1, 1, 51, 0, '2023-10-13 07:07:57', '2023-10-13 07:07:57', 0, ''),
(52, 'Sữa Rửa Mặt Elbon', 260000, 'srm11.jpg', 'Dù dùng mỹ phẩm đắt tiển cỡ nào, nếu dùng không đúng cách bạn vẫn không đạt được kết quả như mong muốn. ', 1, 3, 50, 0, '2023-10-14 13:20:29', '0000-00-00 00:00:00', 0, ''),
